mapper.select:
select no, name, e_nm, nick_nm, age, sex, address1, address2, postno, email, job, sns1_addr, sns2_addr, sns3_addr, memo1, memo2, memo3, telno, cellno
  from pms_info
 where no = #{no}

mapper.update:
 UPDATE pms_info
    SET  name = #{name}
       , e_nm = #{eNm}
       , nick_nm = #{nickNm}
       , age = #{age}
       , sex = #{sex}
       , address1 = #{address1}
       , address2 = #{address2}
       , postno = #{postNo}
       , email = #{email}
       , job = #{job}
       , sns1_addr = #{sns1Addr}
       , sns2_addr = #{sns2Addr}
       , sns3_addr = #{sns3Addr}
       , memo1 = #{memo1}
       , memo2 = #{memo2}
       , memo3 = #{memo3}
       , telno = #{telNo}
       , cellno = #{cellNo}
WHERE no = #{no}
